mapper.update:
UPDATE BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}

mapper.select:

    select
	*
	from
	(
   		 select
   		 /*+index_desc(board pk_board1)*/
    	 rownum rn,bno,title,content,userid,hit,good,board_date,board_updateDate
   		 from board
    	 where rownum <= #{pageNum} * #{amount}
    	 )
    	 where rn > (#{pageNum}-1) * #{amount}
