mapper.update:
UPDATE BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}

mapper.select:

    select
	*
	from
	(
   		 select
   		 /*+index_desc(board pk_board1)*/
    	 rownum rn,bno, userId, title, content, hit, good, board_date, board_updatedate, replycnt 
   		 from board
			where






	rownum <=#{pageNum}*#{amount}
	)
	where rn >(#{pageNum}-1)*#{amount}

mapper.update:
update board
set replycnt = replycnt+#{amount}
where bno = #{bno}
