mapper.select:
select bno, USER_ID as userId, title, content, hit, good, BOARD_DATE as boardDate,
  BOARD_UPDATE_DATE as boardUpdateDate, REPLY_CNT as replyCnt
   from board where bno=#{bno}

mapper.update:
UPDATE BOARD SET
HIT = HIT+1
WHERE BNO = #{bno}

mapper.select:

    select
	bno, userId, title, content, hit, good, boardDate, boardUpdateDate, replyCnt
	from
	(
   		 select
   		 /*+index_desc(board pk_board1)*/
    	 rownum rn, bno, USER_ID as userId, title, content, hit, good, 
    	 BOARD_DATE as boardDate, BOARD_UPDATE_DATE as boardUpdateDate, REPLY_CNT as replyCnt
   		 from board
			where






	rownum <=#{pageNum}*#{amount}
	)
	where rn >(#{pageNum}-1)*#{amount}

mapper.update:
update board
set REPLY_CNT = REPLY_CNT + #{amount}
where bno = #{bno}
